import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.css';


function OnlyConflictsLayout(props) {

    // Acts like the parameters of the function
    const { coursesList, conflictsOne, conflictsTwo, conflictsThree } = props

    const [CourseValue, setCourseValue] = useState("None");

    // Converts CRN to a course number
    function course_name(course){
        const current_course = coursesList.find(c => c.CRN === course);
        const course_name = current_course.course_number;
        return course_name;
    }

    const handleCourseFilter = (event) => {
        setCourseValue(event.target.value);
    }

    function removeDuplicates(coursesList) {
        let arr = [];
        for (let c in coursesList) {
            console.log(c);
            if (!arr.includes(c["course_number"])) {
                arr.push(c["course_number"]);
            }
        }

        return arr;
    }

    function filterArray(report, filterValue) {
        if (filterValue != "None") {
            let filteredConflicts = report.filter(conflict =>
                {
                    if (course_name(conflict[0]) == filterValue) {
                        return true;
                    }
                    return false;
                }).map(conflict => {
                    return conflict;
                })
            return filteredConflicts; 
        } 
        else {
            return report;
        }        
    }


    return (
        <div>
            <div class="w3-sidebar w3-border w3-bar-block" style={{width: "10%", height: "5%", position: "relative", float: "left", left: 10, bottom:"35.0%"}}>
                    <select id="Choose" onChange={handleCourseFilter}>
                        <option value="None">None</option>
						{removeDuplicates(coursesList).map(course =>
							<option value={course}>{course}</option>	
						)}

					</select>
            </div>

            <div style={{marginLeft: "150px", overflow: "scroll"}}>
                <p id="demo1"></p>
                <div class="row" style={{flexWrap:"nowrap"}}>
                    <div class="col">
                        <font color="red"><h1>Conflict Report 1</h1></font>
                        <table class="table table-striped table-bordered table-responsive table-sm">
                            <thead>
                                <tr>
                                    <th>Course</th>
                                    <th>CRN</th>
                                    <th>UID</th>
                                    <th>Last</th>
                                    <th>First</th>
                                    <th>Conflict</th>
                                </tr>
                            </thead>
                            <tbody>  
                                {(filterArray(conflictsOne, CourseValue)).map(conflict =>
                                    <tr>
                                        <td>COMP {course_name(conflict[0])}</td>
                                        <td>{conflict[0]}</td>
                                        <td>{conflict[1]}</td>
                                        <td>{conflict[2]}</td>
                                        <td>{conflict[3]}</td>
                                        <td>{conflict[4]}</td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>

                    <div class="col">
                        <font color="red"><h1>Conflict Report 2</h1></font>
                        <table class="table table-striped table-bordered table-responsive table-sm">
                            <thead>
                                <tr>
                                    <th>Course</th>
                                    <th>CRN</th>
                                    <th>UID</th>
                                    <th>Last</th>
                                    <th>First</th>
                                    <th>Conflict</th>
                                </tr>
                            </thead>
                            <tbody>
                                {filterArray(conflictsTwo, CourseValue).map(conflict => 
                                    <tr> 
                                        <td>{course_name(conflict[0])}</td>
                                        <td>{conflict[0]}</td> {/* Each index represent the CRN, UID, First Name, Last Name, and Conflict */}
                                        <td>{conflict[1]}</td>
                                        <td>{conflict[2]}</td>
                                        <td>{conflict[3]}</td>
                                        <td>{conflict[4]}</td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>

                    <div class="col">
                        <font color="red"><h1>Conflict Report 3</h1></font>
                        <table class="table table-striped table-bordered table-responsive table-sm">
                            <thead>
                                <tr>
                                    <th>Course</th>
                                    <th>CRN</th>
                                    <th>UID</th>
                                    <th>Last</th>
                                    <th>First</th>
                                    <th>Conflict</th>
                                </tr>
                            </thead>
                            <tbody>
                                {filterArray(conflictsThree, CourseValue).map(conflict => 
                                    <tr> 
                                        <td>{course_name(conflict[0])}</td>
                                        <td>{conflict[0]}</td> {/* Each index represent the CRN, UID, First Name, Last Name, and Conflict */}
                                        <td>{conflict[1]}</td>
                                        <td>{conflict[2]}</td>
                                        <td>{conflict[3]}</td>
                                        <td>{conflict[4]}</td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>    
                </div>   
            </div>
        </div>
    );
}
export { OnlyConflictsLayout };
